import { Component, Input, OnInit } from '@angular/core';
import { FormControl } from '@angular/forms';

@Component({
  selector: 'app-customizador',
  templateUrl: './customizador.component.html',
  styleUrls: ['./customizador.component.scss'],
})
export class CustomizadorComponent  implements OnInit {

  @Input() control!: FormControl;
  @Input() type!: string;
  @Input() label!: string;
  @Input() autocomplete!: string;

  isPassword!: boolean;
  hide: boolean = true;

  constructor() { }

  ngOnInit() {
    if(this.type == 'password') this.isPassword = true;
  }

  showOrHidePassword(){
    this.hide = !this.hide;
    if(this.hide) this.type = 'password';
    else this.type = 'text';

  }

}
<ion-item class="custom-imput" lines="none">
  <ion-icon color="primary"  slot="start" name="icon"></ion-icon>

  <ion-input 
   [type]="type"
   [autocomplete]="autocomplete"
   [label]="label"
   [formControl]="control"
   label-placement="floating"
   ></ion-input>

   <ion-button *ngIf="isPassword" (click)="showOrHidePassword()" slot="end" fill="clear" shape="round">
    <ion-icon slot="icon-only" [name]="hide ? 'eye-outline' : 'eye-off-outline'"></ion-icon>
   </ion-button>
</ion-item>
